import React from "react";
import styled, { css } from "styled-components";

interface ButtonProps {
  label: string;
  disabled?: boolean;
}

const ButtonContainer = styled.button.attrs((props: ButtonProps) => ({
  disabled: props.disabled ?? false,
}))<ButtonProps>`
  padding: 10px 20px;
  font-size: 16px;
  cursor: pointer;
  border: none;
  border-radius: 5px;

  ${(props) =>
    props.disabled &&
    css`
      background-color: #dddddd;
      color: #888888;
      cursor: not-allowed;
    `}
`;

const Button: React.FC<ButtonProps> = ({ label, disabled }) => {
  return <ButtonContainer disabled={disabled}>{label}</ButtonContainer>;
};

export default Button;
